import React from 'react';
import logo from './logo.svg';
import './App.css';
import './style.css';
import './styling.css';

class App extends React.Component {
state = {
 result: []
}

componentDidMount(){
this.getResults();
}

getResults= _ =>{
 fetch('http://localhost:4000/MyGuests')
  .then(response => response.json()) 
  .then(response => this.setState({result: response.data}))
  .catch(err => console.error(err))
}
renderResults = ({name,email,comments,reg_date}) => <div>{name}:|:  {email}  {comments}  {reg_date}</div>
render() {
const {result} = this.state;
  return (
    <div className="App">
      <p> {result.map(this.renderResults)}</p>
    </div>
  );
}
}

export default App;
